rule.definition:
= load(
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  local!selectedId,
  with(
    local!datasubset: todatasubset(
      ri!paymentDetails,
      local!gridSelection.pagingInfo
    ),
    {
      #"SYSTEM_SYSRULES_sectionLayout"(
        label: "Payment Activities",
        firstColumnContents: {
          #"SYSTEM_SYSRULES_gridField"(
            label: "",
            totalCount: local!datasubset.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Activity Type",
                field: "activityType",
                data: index(
                  local!datasubset.data,
                  "activityType",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Description",
                field: "activityDescripiton",
                data: index(
                  local!datasubset.data,
                  "activityDescripiton",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Screening ID",
                field: "screeningId",
                data: index(
                  local!datasubset.data,
                  "screeningId",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Start Date",
                field: "startDate",
                data: index(
                  local!datasubset.data,
                  "startDate",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Completed Date",
                field: "completedDate",
                data: index(
                  local!datasubset.data,
                  "completedDate",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Actual Amount",
                field: "actualAmount",
                data: dollar(
                  index(
                    local!datasubset.data,
                    "actualAmount",
                    {}
                  ),
                  2,
                  1
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Comments",
                field: "comments",
                data: index(
                  local!datasubset.data,
                  "comments",
                  {}
                )
              ),
              if(
                ri!selectable,
                {},
                #"SYSTEM_SYSRULES_gridImageColumn"(
                  label: "",
                  field: "paymentActivityId",
                  data: apply(
                    #"_a-0000dd3a-dada-8000-daab-640000640000_11015"(
                      local!selectedId,
                      _
                    ),
                    index(
                      local!datasubset.data,
                      "paymentActivityId",
                      {}
                    )
                  ),
                  size: "ICON"
                )
              )
            },
            identifiers: index(
              local!datasubset.data,
              "paymentActivityId",
              {}
            ),
            value: local!gridSelection,
            saveInto: {
              local!gridSelection,
              a!save(
                local!selectedId,
                index(
                  save!value,
                  "selected",
                  null
                )
              )
            },
            selection: ri!selectable,
            validations: if(
              isnull(
                local!selectedId
              ),
              "",
              if(
                length(
                  local!selectedId
                ) > 1,
                "Limit of one selection",
                ""
              )
            )
          )
        },
        secondColumnContents: {
          /* Add components here for a two-column section */

        },
        validations: {},
        isCollapsible: true
      ),
      if(
        #"_a-0000ddcb-815e-8000-daab-640000640000_18403"(
          input: local!selectedId
        ),
        {},
        with(
          local!selectedPaymentDetail: #"_a-0000ddcc-a4d5-8000-daab-640000640000_18492"(
            id: local!selectedId
          ),
          #"_a-0000dd4f-0404-8000-daab-640000640000_16518"(
            paymentDetail: local!selectedPaymentDetail
          )
        )
      )
    }
  )
)
