rule.definition:
= load(
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  local!paymentAndDetails: #"_a-0000dd3a-dada-8000-daab-640000640000_10921"(
    paymentId: ri!paymentId
  ),
  local!selectedId,
  with(
    local!datasubset: todatasubset(
      local!paymentAndDetails,
      local!gridSelection.pagingInfo
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Payments",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_gridField"(
          label: "",
          totalCount: local!datasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Payment #",
              field: "paymentNumber",
              data: index(
                local!datasubset.data,
                "paymentNumber",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Payment Type",
              field: "paymentType",
              data: index(
                local!datasubset.data,
                "paymentType",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Status",
              field: "paymentStatus",
              data: index(
                local!datasubset.data,
                "paymentStatus",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Earned Amount",
              field: "earnedAmount",
              data: dollar(
                index(
                  local!datasubset.data,
                  "earnedAmount",
                  {}
                ),
                2,
                1
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Requested Amount",
              field: "requestedAmount",
              data: dollar(
                index(
                  local!datasubset.data,
                  "requestedAmount",
                  {}
                ),
                2,
                1
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Paid Amount",
              field: "paymentAmount",
              data: dollar(
                index(
                  local!datasubset.data,
                  "paymentAmount",
                  {}
                ),
                2,
                1
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Paid Date",
              field: "status",
              data: index(
                local!datasubset.data,
                "paidDate",
                {}
              )
            )
          },
          identifiers: index(
            local!datasubset.data,
            "paymentNumber",
            {}
          ),
          value: local!gridSelection,
          saveInto: {
            local!gridSelection,
            a!save(
              local!selectedId,
              index(
                save!value,
                "selected",
                null
              )
            )
          },
          selection: ri!selectable,
          validations: if(
            isnull(
              local!selectedId
            ),
            "",
            if(
              length(
                local!selectedId
              ) > 1,
              "Limit of one selection",
              ""
            )
          )
        )
      },
      secondColumnContents: {
        /* Add components here for a two-column section */

      },
      validations: {}
    )
  )
)
