rule.definition:
= load(
  local!selectedPaymentId,
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  #"SYSTEM_SYSRULES_dashboardLayout"(
    firstColumnContents: {
      /*Header Section*/
      #"_a-0000dd4f-0404-8000-daab-640000640000_16503"(siteId: ri!siteId),
      #"_a-0000dd3a-dada-8000-daab-640000640000_10991"(
        siteId: ri!siteId,
        selectable: false,
        selectedPaymentId: local!selectedPaymentId,
        gridSelection: local!gridSelection
      ),
      if(
        isnull(
          local!selectedPaymentId
        ),
        {},
        with(
          local!payment: #"_a-0000dd3a-dada-8000-daab-640000640000_10921"(
            paymentId: local!selectedPaymentId
          ),
          /*The query rule only returns one row of data while the a!queryEntity returns all*/
          /*local!paymentDetails: rule!CTQG_getSitePaymentDetailsByPaymentId(*/
          /*  paymentId: ri!paymentId*/
          /*),*/
          local!paymentDetails: if(
            isnull(
              local!selectedPaymentId
            ),
            null,
            #"SYSTEM_SYSRULES_queryEntity"(
              entity: #"_a-0000dd3a-dada-8000-daab-640000640000_10986",
              query: #"SYSTEM_SYSRULES_query"(
                filter: #"SYSTEM_SYSRULES_queryFilter"(
                  field: "paymentId",
                  operator: "=",
                  value: local!selectedPaymentId
                ),
                pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                  startIndex: 1,
                  batchSize: - 1,
                  sort: #"SYSTEM_SYSRULES_sortInfo"(
                    field: "completedDate",
                    ascending: false
                  )
                )
              )
            )
          ),
          local!claims: if(
            isnull(
              local!selectedPaymentId
            ),
            null,
            #"SYSTEM_SYSRULES_queryEntity"(
              entity: #"_a-0000dd3a-dada-8000-daab-640000640000_11306",
              query: #"SYSTEM_SYSRULES_query"(
                filter: #"SYSTEM_SYSRULES_queryFilter"(
                  field: "paymentIdentifier",
                  operator: "=",
                  value: local!selectedPaymentId
                ),
                pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                  startIndex: 1,
                  batchSize: - 1,
                  sort: #"SYSTEM_SYSRULES_sortInfo"(
                    field: "createdDate",
                    ascending: false
                  )
                )
              )
            )
          ),
          {
            if(
              length(
                local!payment
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_10931"(
                payment: local!payment
              ),
              {}
            ),
            if(
              length(
                local!paymentDetails.data
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_10883"(
                paymentDetails: local!paymentDetails.data
              ),
              {}
            ),
            if(
              length(
                local!claims.data
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_11574"(
                claims: local!claims.data
              ),
              {}
            )
          }
        )
      )
    },
    secondColumnContents: {}
  )
)
