rule.definition:
= load(
  local!siteBudgets: if(
    isnull(
      ri!siteIdentifier
    ),
    null,
    #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dd3a-dada-8000-daab-640000640000_11014",
      query: #"SYSTEM_SYSRULES_query"(
        filter: #"SYSTEM_SYSRULES_queryFilter"(
          field: "id",
          operator: "=",
          value: ri!siteIdentifier
        ),
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: - 1,
          sort: #"SYSTEM_SYSRULES_sortInfo"(
            field: "siteBudgetId",
            ascending: false
          )
        )
      )
    )
  ),
  local!selectedId,
  if(
    or(
      isnull(
        ri!siteIdentifier
      ),
      length(
        local!siteBudgets.data
      ) < 1
    ),
    #"SYSTEM_SYSRULES_textField"(
      readOnly: true,
      label: "There are no budgets"
    ),
    with(
      local!datasubset: todatasubset(
        local!siteBudgets.data,
        ri!gridSelection.pagingInfo
      ),
      #"SYSTEM_SYSRULES_sectionLayout"(
        label: "Site Budget",
        firstColumnContents: {
          #"SYSTEM_SYSRULES_gridField"(
            label: "",
            totalCount: local!datasubset.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Budget Identifier",
                field: "siteBudgetId",
                data: index(
                  local!datasubset.data,
                  "siteBudgetId",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Split Indicator",
                field: "splitIndicator",
                data: index(
                  local!datasubset.data,
                  "splitIndicator",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Visit Name",
                field: "visitName",
                data: index(
                  local!datasubset.data,
                  "visitName",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Visit Type",
                field: "visitType",
                data: index(
                  local!datasubset.data,
                  "visitType",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Version",
                field: "version",
                data: index(
                  local!datasubset.data,
                  "version",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Original Amount",
                field: "originalAmount",
                data: dollar(
                  index(
                    local!datasubset.data,
                    "originalAmount",
                    {}
                  ),
                  2,
                  1
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Exception Amount",
                field: "exceptionAmount",
                data: dollar(
                  index(
                    local!datasubset.data,
                    "exceptionAmount",
                    {}
                  ),
                  2,
                  1
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Description",
                field: "description",
                data: index(
                  local!datasubset.data,
                  "description",
                  {}
                )
              ),
              if(
                ri!selectable,
                {},
                #"SYSTEM_SYSRULES_gridImageColumn"(
                  label: "",
                  field: "siteBudgetId",
                  data: apply(
                    #"_a-0000dd3a-dada-8000-daab-640000640000_11015"(
                      ri!selectedBudgetId,
                      _
                    ),
                    local!datasubset.data.siteBudgetId
                  ),
                  size: "ICON"
                )
              )
            },
            identifiers: index(
              local!datasubset.data,
              "siteBudgetId",
              {}
            ),
            value: ri!gridSelection,
            saveInto: {
              ri!gridSelection,
              a!save(
                local!selectedId,
                index(
                  save!value,
                  "selected",
                  null
                )
              ),
              a!save(
                target: ri!selectedBudgetId,
                value: if(
                  length(
                    index(
                      save!value,
                      "selected",
                      null
                    )
                  ) > 1,
                  null,
                  index(
                    save!value,
                    "selected",
                    null
                  )
                )
              )
            },
            selection: ri!selectable,
            validations: if(
              isnull(
                local!selectedId
              ),
              "",
              if(
                length(
                  local!selectedId
                ) > 1,
                "Limit of one selection",
                ""
              )
            )
          )
        },
        secondColumnContents: {
          /* Add components here for a two-column section */

        },
        validations: {},
        isCollapsible: true
      )
    )
  )
)
