rule.definition:
= load(
  local!selectedBudgetId,
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  #"SYSTEM_SYSRULES_dashboardLayout"(
    firstColumnContents: {
      /*Header Section*/
      #"_a-0000dd4f-0404-8000-daab-640000640000_16503"(siteId: ri!siteIdentifier),
      #"_a-0000dd3a-dada-8000-daab-640000640000_11004"(
        siteIdentifier: ri!siteIdentifier,
        selectable: false,
        gridSelection: local!gridSelection,
        selectedBudgetId: local!selectedBudgetId
      ),
      if(
        isnull(
          local!selectedBudgetId
        ),
        {},
        with(
          local!budget: #"_a-0000dd3a-dada-8000-daab-640000640000_11414"(
            budgetId: local!selectedBudgetId
          ),
          local!budgetSplit: if(
            isnull(
              local!selectedBudgetId
            ),
            null,
            #"SYSTEM_SYSRULES_queryEntity"(
              entity: #"_a-0000dd3a-dada-8000-daab-640000640000_11417",
              query: #"SYSTEM_SYSRULES_query"(
                filter: #"SYSTEM_SYSRULES_queryFilter"(
                  field: "siteBudgetId",
                  operator: "=",
                  value: local!selectedBudgetId
                ),
                pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
                  startIndex: 1,
                  batchSize: - 1
                )
              )
            )
          ),
          {
            if(
              length(
                local!budget
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_11421"(
                budget: local!budget
              ),
              {}
            ),
            if(
              length(
                local!budgetSplit.data
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_11447"(
                budgetSplit: local!budgetSplit.data
              ),
              {}
            )
          }
        )
      )
    },
    secondColumnContents: {}
  )
)
