rule.definition:
= load(
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  local!selectedId,
  with(
    local!datasubset: todatasubset(
      ri!budgetSplit,
      local!gridSelection.pagingInfo
    ),
    #"SYSTEM_SYSRULES_sectionLayout"(
      label: "Split Details",
      firstColumnContents: {
        #"SYSTEM_SYSRULES_gridField"(
          label: "",
          totalCount: local!datasubset.totalCount,
          columns: {
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Contract",
              field: "contract",
              data: index(
                local!datasubset.data,
                "contract",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Payee Account Name",
              field: "payeeAccount",
              data: index(
                local!datasubset.data,
                "payeeAccount",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Payee Last Name",
              field: "payeeLastName",
              data: index(
                local!datasubset.data,
                "payeeLastName",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Payee First Name",
              field: "payeeFirstName",
              data: index(
                local!datasubset.data,
                "payeeFirstName",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Split %",
              field: "splitPercentage",
              data: index(
                local!datasubset.data,
                "splitPercentage",
                {}
              )
            ),
            #"SYSTEM_SYSRULES_gridTextColumn"(
              label: "Split Amount",
              field: "splitAmount",
              data: dollar(
                index(
                  local!datasubset.data,
                  "splitAmount",
                  {}
                ),
                2,
                1
              )
            )
          },
          identifiers: index(
            local!datasubset.data,
            "splitDetailId",
            {}
          ),
          value: local!gridSelection,
          saveInto: {
            local!gridSelection,
            a!save(
              local!selectedId,
              index(
                save!value,
                "selected",
                null
              )
            )
          },
          selection: ri!selectable,
          validations: if(
            isnull(
              local!selectedId
            ),
            "",
            if(
              length(
                local!selectedId
              ) > 1,
              "Limit of one selection",
              ""
            )
          )
        )
      },
      secondColumnContents: {
        /* Add components here for a two-column section */

      },
      validations: {},
      isCollapsible: true
    )
  )
)
