rule.definition:
= load(
  local!activities: if(
    isnull(
      ri!siteId
    ),
    null,
    #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000dd4f-0404-8000-daab-640000640000_16525",
      query: #"SYSTEM_SYSRULES_query"(
        filter: #"SYSTEM_SYSRULES_queryFilter"(
          field: "siteId",
          operator: "=",
          value: ri!siteId
        ),
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: - 1
        )
      )
    )
  ),
  local!selectedIds,
  if(
    or(
      isnull(
        ri!siteId
      ),
      length(
        local!activities.data
      ) < 1
    ),
    #"SYSTEM_SYSRULES_textField"(
      readOnly: true,
      label: "There are no Activities"
    ),
    with(
      local!datasubset: todatasubset(
        local!activities.data,
        ri!gridSelection.pagingInfo
      ),
      #"SYSTEM_SYSRULES_sectionLayout"(
        label: "Payment Activities",
        firstColumnContents: {
          #"SYSTEM_SYSRULES_gridField"(
            label: "",
            totalCount: local!datasubset.totalCount,
            columns: {
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Activity Type",
                field: "activityType",
                data: index(
                  local!datasubset.data,
                  "activityType",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Description",
                field: "activityDescripiton",
                data: index(
                  local!datasubset.data,
                  "activityDescripiton",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Screening ID",
                field: "screeningId",
                data: index(
                  local!datasubset.data,
                  "screeningId",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Start Date",
                field: "startDate",
                data: index(
                  local!datasubset.data,
                  "startDate",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Completed Date",
                field: "completedDate",
                data: index(
                  local!datasubset.data,
                  "completedDate",
                  {}
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Actual Amount",
                field: "actualAmount",
                data: dollar(
                  index(
                    local!datasubset.data,
                    "actualAmount",
                    {}
                  ),
                  2,
                  1
                )
              ),
              #"SYSTEM_SYSRULES_gridTextColumn"(
                label: "Comments",
                field: "comments",
                data: index(
                  local!datasubset.data,
                  "comments",
                  {}
                )
              ),
              if(
                ri!selectable,
                {},
                #"SYSTEM_SYSRULES_gridImageColumn"(
                  label: "",
                  field: "paymentActivityId",
                  data: apply(
                    #"_a-0000dd3a-dada-8000-daab-640000640000_11015"(
                      ri!selectedId,
                      _
                    ),
                    index(
                      local!datasubset.data,
                      "paymentActivityId",
                      {}
                    )
                  ),
                  size: "ICON"
                )
              )
            },
            identifiers: index(
              local!datasubset.data,
              "paymentActivityId",
              {}
            ),
            value: ri!gridSelection,
            saveInto: {
              ri!gridSelection,
              a!save(
                target: local!selectedIds,
                value: index(
                  save!value,
                  "selected",
                  null
                )
              ),
              a!save(
                target: ri!selectedId,
                value: if(
                  length(
                    index(
                      save!value,
                      "selected",
                      null
                    )
                  ) > 1,
                  null,
                  index(
                    save!value,
                    "selected",
                    null
                  )
                )
              )
            },
            selection: ri!selectable,
            validations: if(
              isnull(
                ri!selectedId
              ),
              "",
              if(
                length(
                  ri!selectedId
                ) > 1,
                "Limit of one selection",
                ""
              )
            )
          )
        },
        secondColumnContents: {
          /* Add components here for a two-column section */

        },
        validations: {},
        isCollapsible: true
      )
    )
  )
)
