rule.definition:
= load(
  local!selectedActivityId,
  local!gridSelection: #"SYSTEM_SYSRULES_gridSelection"(
    pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
      startIndex: 1,
      batchsize: 10
    )
  ),
  #"SYSTEM_SYSRULES_dashboardLayout"(
    firstColumnContents: {
      /*Header Section*/
      #"_a-0000dd4f-0404-8000-daab-640000640000_16503"(siteId: ri!siteId),
      #"_a-0000dd4f-0404-8000-daab-640000640000_16530"(
        siteId: ri!siteId,
        selectable: false,
        gridSelection: local!gridSelection,
        selectedId: local!selectedActivityId
      ),
      if(
        isnull(
          local!selectedActivityId
        ),
        {},
        with(
          local!activity: #"_a-0000ddcc-a4d5-8000-daab-640000640000_18487"(
            id: local!selectedActivityId
          ),
          {
            if(
              length(
                local!activity
              ) > 0,
              #"_a-0000dd3a-dada-8000-daab-640000640000_13596"(
                activitiesDetail: local!activity
              ),
              {}
            )
          }
        )
      )
    },
    secondColumnContents: {}
  )
)
