rule.definition:
with(
  /*We use query aggregation to group by status. When using aggregation, it*/
  /*returns only the data fields being aggregated.*/
  /*The count is so that we can pass this into the faceOption dataCount input*/
  /*which will tell us how many of each type of status there is.*/
  local!region: #"SYSTEM_SYSRULES_queryEntity"(
    entity: #"_a-0000dd4f-0404-8000-daab-640000640000_16600",
    query: #"SYSTEM_SYSRULES_query"(
      aggregation: #"SYSTEM_SYSRULES_queryAggregation"(
        aggregationColumns: {
          #"SYSTEM_SYSRULES_queryAggregationColumn"(
            field: "region",
            isGrouping: true
          ),
          #"SYSTEM_SYSRULES_queryAggregationColumn"(
            field: "siteIdentifier",
            alias: "count",
            aggregationFunction: "COUNT"
          )
        }
      ),
      filter: #"SYSTEM_SYSRULES_queryFilter"(
        field: "hasAccess",
        operator: "includes",
        value: #"30601f19-d155-4551-abfe-f83286eae862"()
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: - 1
      )
    )
  ),
  if(
    #"_a-0000ddcb-815e-8000-daab-640000640000_18403"(
      local!region.data
    ),
    {},
    #"SYSTEM_SYSRULES_facet"(
      name: "Region",
      options: apply(
        #"_a-0000dd4f-0404-8000-daab-640000640000_16715",
        merge(
          local!region.data.region,
          enumerate(
            local!region.totalCount
          ) + 1,
          local!region.data.count
        )
      ),
      isVisible: true
    )
  )
)
