rule.definition:
/*
First it will query the values to see if it returns any values
If length < 1 (empty list), then it returns empty list
Otherwise query again and retrieve list of investigatorIdentifiers
*/
load(
  local!data: if(
    length(
      #"SYSTEM_SYSRULES_queryEntity"(
        entity: #"_a-0000def6-d094-8000-9ba2-011c48011c48_37098",
        query: #"SYSTEM_SYSRULES_query"(
          logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
            operator: "AND",
            filters: {
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "expirationDate",
                operator: "<=",
                value: today() + ri!toDays
              ),
              #"SYSTEM_SYSRULES_queryFilter"(
                field: "expirationDate",
                operator: ">=",
                value: today() + ri!fromDays
              )
            }
          ),
          pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
            startIndex: 1,
            batchSize: - 1
          )
        )
      ).data
    ) < 1,
    {},
    #"SYSTEM_SYSRULES_queryEntity"(
      entity: #"_a-0000def6-d094-8000-9ba2-011c48011c48_37098",
      query: #"SYSTEM_SYSRULES_query"(
        logicalExpression: #"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator: "AND",
          filters: {
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "expirationDate",
              operator: "<=",
              value: today() + ri!toDays
            ),
            #"SYSTEM_SYSRULES_queryFilter"(
              field: "expirationDate",
              operator: ">=",
              value: today() + ri!fromDays
            )
          }
        ),
        pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
          startIndex: 1,
          batchSize: - 1
        )
      )
    ).data.investigatorIdentifier
  ),
  local!uniqueIdentifiers: union(
    local!data,
    local!data
  ),
  filter(
    #"_a-0000def6-d094-8000-9ba2-011c48011c48_37137",
    local!uniqueIdentifiers
  )
)
