rule.definition:
=load(
  local!gridPI:#"SYSTEM_SYSRULES_pagingInfo"(
    1,
    10,
    #"SYSTEM_SYSRULES_sortInfo"(field:"c0")),
  local!statusFilter:-1,
  with(
    local!tasksForUser:a!queryProcessAnalytics(
      report:#"_a-0000df4f-3e48-8000-9ba2-011c48011c48_62059",
      query:#"SYSTEM_SYSRULES_query"(
        logicalExpression:#"SYSTEM_SYSRULES_queryLogicalExpression"(
          operator:"AND",
          filters:{
            if(local!statusFilter=-1, null,
          #"SYSTEM_SYSRULES_queryFilter"(field:"c5",operator:"=",value:local!statusFilter)),
          #"SYSTEM_SYSRULES_queryFilter"(field:"c7",operator:"includes",value:loggedInUser())
          }
        ), 
        pagingInfo:local!gridPI
      )
    ),
    {
      #"SYSTEM_SYSRULES_dropdownField"(
        label: "Status",
        choiceLabels: {"All", "Assigned", "Accepted"},
        choiceValues: {-1,0,1},
        value: local!statusFilter,
        saveInto: {
          local!statusFilter,
          a!save(local!gridPI.startIndex, 1)
        }
      ),
      #"SYSTEM_SYSRULES_gridField"(
        label:"Current Tasks",
        instructions:"",
        totalCount:local!tasksForUser.totalCount,
        columns:if(isnull(local!tasksForUser.columnConfigs),"You have no tasks", apply(
          #"_a-0000da5f-caaa-8000-9ba2-011c48011c48_2897"(columnConfig:_, data:local!tasksForUser.data, showLink:true),
          local!tasksForUser.columnConfigs
        )),
        value:local!gridPI,
        saveInto:local!gridPI
      )
    }
  )
)
