rule.definition:
load(
  local!taskReport: a!queryProcessAnalytics(
    report: #"_a-0000dd3a-dada-8000-daab-640000640000_11333",
    query: #"SYSTEM_SYSRULES_query"(
      filter: #"SYSTEM_SYSRULES_queryFilter"(
        field: "c4",
        operator: "=",
        value: ri!processId
      ),
      pagingInfo: #"SYSTEM_SYSRULES_pagingInfo"(
        startIndex: 1,
        batchSize: 1
      )
    )
  ),
  local!taskId: if(
    #"_a-0000dd3a-dada-8000-daab-640000640000_11122"(
      local!taskReport.data
    ),
    null,
    index(
      local!taskReport.data,
      "c5",
      null
    )
  ),
  tointeger(
    local!taskId
  )
)
